import React, { useState } from 'react';

function AddNewProblem({ addProblem }) {
  const [name, setName] = useState('');
  const [difficulty, setDifficulty] = useState('Easy');
  const [description, setDescription] = useState('');
  const [testCases, setTestCases] = useState([{ input: '', output: '' }]);

  const handleAddTestCase = () => {
    setTestCases([...testCases, { input: '', output: '' }]);
  };

  const handleTestCaseChange = (index, field, value) => {
    const newTestCases = [...testCases];
    newTestCases[index][field] = value;
    setTestCases(newTestCases);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addProblem({
      name,
      difficulty,
      description,
      testCases
    });
    setName('');
    setDifficulty('Easy');
    setDescription('');
    setTestCases([{ input: '', output: '' }]);
  };

  return (
    <div>
      <h2>Add New Problem</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Problem Name: </label>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
        </div>
        <div>
          <label>Difficulty: </label>
          <select value={difficulty} onChange={(e) => setDifficulty(e.target.value)} required>
            <option value="Easy">Easy</option>
            <option value="Medium">Medium</option>
            <option value="Hard">Hard</option>
          </select>
        </div>
        <div>
          <label>Description: </label>
          <textarea value={description} onChange={(e) => setDescription(e.target.value)} required />
        </div>
        <div>
          <h3>Test Cases</h3>
          {testCases.map((testCase, index) => (
            <div key={index}>
              <label>Input: </label>
              <input type="text" value={testCase.input} onChange={(e) => handleTestCaseChange(index, 'input', e.target.value)} required />
              <label>Output: </label>
              <input type="text" value={testCase.output} onChange={(e) => handleTestCaseChange(index, 'output', e.target.value)} required />
            </div>
          ))}
          <button type="button" onClick={handleAddTestCase}>Add Test Case</button>
        </div>
        <button type="submit">Add Problem</button>
      </form>
    </div>
  );
}

export default AddNewProblem;
