import React, { useState } from "react"; // IMPORT useState FOR STATE MANAGEMENT
import { useNavigate, Link } from "react-router-dom"; // IMPORT useNavigate AND Link FROM REACT-ROUTER-DOM
import axios from "axios"; // IMPORT axios FOR MAKING API REQUESTS
import { ToastContainer, toast } from "react-toastify"; // IMPORT TOASTIFY FOR NOTIFICATIONS
import 'react-toastify/dist/ReactToastify.css'; // IMPORT TOASTIFY CSS
import './LoginPage.css';
import googleLogo from "./GoogleLogo.png";

function SignupPage() {
  const navigate = useNavigate(); // GET THE NAVIGATION FUNCTION
  const [inputValue, setInputValue] = useState({ // STATE TO STORE FORM INPUTS
    fullName: "", 
    email: "",
    password: "",
  });
  
  const { fullName, email, password } = inputValue; // DESTRUCTURE STATE VARIABLES

  // HANDLE INPUT CHANGE
  const handleOnChange = (e) => {
    const { name, value } = e.target;
    setInputValue({
      ...inputValue,
      [name]: value,
    });
  };

  // DISPLAY ERROR TOAST
  const handleError = (err) =>
    toast.error(err, {
      position: "bottom-left",
    });

  // DISPLAY SUCCESS TOAST
  const handleSuccess = (msg) =>
    toast.success(msg, {
      position: "bottom-left",
    });

  // HANDLE FORM SUBMISSION
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const { data } = await axios.post(
        "http://localhost:5000/auth/signup", // BACKEND SIGNUP ROUTE
        { ...inputValue },
        { withCredentials: true }
      );
      const { success, message } = data; // DESTRUCTURE RESPONSE
      if (success) { // HANDLE SUCCESS
        handleSuccess(message);
        setTimeout(() => {
          navigate("/"); // REDIRECT TO HOME PAGE ON SUCCESS
        }, 1000);
      } else { // HANDLE ERROR
        handleError(message);
      }
    } catch (error) {
      console.log(error);
    }
    setInputValue({
      ...inputValue,
      fullName: "", // RESET fullName FIELD
      email: "",
      password: "",
    });
  };

  return (
    <div className="LoginPage">
      <div className="header">
        <img src="/Assets/logo.png" alt="Logo" />
      </div>
      <div className="content">
        <h2>Sign Up</h2>
        <form onSubmit={handleSubmit}> {/* ADDED FORM SUBMISSION */}
          <div className="boxes">
            <h5>Full Name</h5> {/* FIELD LABEL */}
            <input
              type="text"
              name="fullName"
              value={fullName}
              placeholder="Enter your full name"
              onChange={handleOnChange} // HANDLE INPUT CHANGE
            />
            <h5>Email</h5>
            <input
              type="email" // CHANGED TYPE TO EMAIL
              name="email"
              value={email}
              placeholder="Enter your email"
              onChange={handleOnChange} // HANDLE INPUT CHANGE
            />
            <h5>Password</h5>
            <input
              type="password" // CHANGED TYPE TO PASSWORD
              name="password"
              value={password}
              placeholder="Enter your password"
              onChange={handleOnChange} // HANDLE INPUT CHANGE
            />
            <button className="loginPg" type="submit">Sign Up</button> {/* ADDED TYPE SUBMIT */}
            <p className="checking">or</p>
            <button className="google">
              <img src={googleLogo} alt="Google Logo" className="google-logo" />Continue with Google 
            </button>
          </div>
        </form>
        <p className="last">Already have an account?</p>
        <Link to="/login" className="last">Log In</Link> {/* UPDATED LINK */}
      </div>
      <ToastContainer /> {/* ADDED TOAST CONTAINER */}
    </div>
  );
}

export default SignupPage;
