import React, { useState, useEffect } from 'react';
import { Link, useParams } from "react-router-dom";
import axios from 'axios';
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';
import './CompilerPageCSS.css';

function CompilerPage() {
    const { problemId } = useParams();
    const [problem, setProblem] = useState(null);
    const [selectedLanguage, setSelectedLanguage] = useState("cpp");
    const [code, setCode] = useState({
        lang: "cpp",
        code: "",
    });
    const [manualTestCase, setManualTestCase] = useState("");
    const [output, setOutput] = useState("");

    useEffect(() => {
        const fetchProblem = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/code/${problemId}`);
                setProblem(response.data);
            } catch (error) {
                console.error('Error fetching problem data:', error);
                toast.error('Error fetching problem data', {
                    position: "top-center",
                });
            }
        };

        fetchProblem();
    }, [problemId]);

    const handleLanguageChange = (e) => {
        const lang = e.target.value;
        setSelectedLanguage(lang);
        setCode((prevState) => ({ ...prevState, lang }));
    };

    const handleCodeChange = (e) => {
        const codeValue = e.target.value;
        setCode((prevState) => ({ ...prevState, code: codeValue }));
    };

    const handleManualTestCaseChange = (e) => {
        setManualTestCase(e.target.value);
    };

    const handleError = (err) => {
        toast.error(err, {
            position: "top-center",
        });
        setOutput(`Error: ${err}`);
    };

    const handleSuccess = (msg) => {
        toast.success(msg, {
            position: "top-center",
        });
        setOutput(`Passed: ${msg}`);
    };

    const handleRun = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post(
                "http://localhost:8000/run",
                { ...code, testCase: manualTestCase }
            );
            const { success, message, output } = response.data;

            if (success) {
                handleSuccess(message);
                setOutput(output);
            } else {
                handleError(message);
            }
        } catch (error) {
            const errorMessage = error.response?.data || "An error occurred";
            handleError(errorMessage);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post(
                "http://localhost:8000/submit",
                { ...code }
            );
            const { success, message, output } = response.data;

            if (success) {
                handleSuccess(message);
                setOutput(output);
            } else {
                handleError(message);
            }
        } catch (error) {
            const errorMessage = error.response?.data || "An error occurred";
            handleError(errorMessage);
        }
    };

    return (
        <div>
            {/* HEADER */}
            <div className="headerComp">
                <div className="left">
                    <img src="/Assets/logo.png" alt="Logo" />
                </div>
                <div className="right">
                    <Link to="/problems">Problems</Link>
                    <Link to="/"><img src="/Assets/ProfileLogo.png" className="profileLogo" alt="Logo" /></Link>
                </div>
            </div>

            {/* CONTENT */}
            <div className="contentCompiler">
                <div className="ProblemDesc">
                    {problem ? (
                        <div>
                            <h1>{`${problem.number}: ${problem.title}`}</h1>
                            <p>{problem.description}</p>
                            {problem.testCases.map((testCase, index) => (
                                <div key={index}>
                                    <h3>{`Test Case ${index + 1}`}</h3>
                                    <p><strong>Input:</strong> {testCase.input}</p>
                                    <p><strong>Output:</strong> {testCase.output}</p>
                                    <p><strong>Explanation:</strong> <br /> {testCase.explanation}</p>
                                </div>
                            ))}
                            <p>{`Accepted : ${problem.acceptance_rate}`}</p>
                        </div>
                    ) : (
                        <p>Loading problem...</p>
                    )}
                </div>
                <div className="Comp">
                    <div className="firstTop">
                        <div className="actionButtons">
                            <button className="runButton" onClick={handleRun}>Run</button>
                            <button className="submitButton" onClick={handleSubmit}>Submit</button>
                        </div>
                        <div className="rightSide">
                            <div className="editorial"><Link to="/">Editorials</Link></div>
                            <div className="dropdown">
                                <select id="languageSelect" value={selectedLanguage} onChange={handleLanguageChange}>
                                    <option value="js">JavaScript</option>
                                    <option value="py">Python</option>
                                    <option value="java">Java</option>
                                    <option value="cpp">C++</option>
                                    <option value="cs">C#</option>
                                    <option value="rb">Ruby</option>
                                    <option value="go">Go</option>
                                    <option value="swift">Swift</option>
                                    <option value="kt">Kotlin</option>
                                </select>
                            </div>
                        </div>
                    </div>
                    {/* Code Editor */}
                    <div className="codeEditor">
                        <textarea
                            className="codeInput"
                            value={code.code}
                            onChange={handleCodeChange}
                            placeholder="Write your code here..."
                        />
                    </div>
                    {/* Input Box */}
                    <div className="manualTestCaseInput">
                        <textarea
                            className="manualTestCase"
                            value={manualTestCase}
                            onChange={handleManualTestCaseChange}
                            placeholder="Enter your test cases here..."
                        />
                    </div>
                    {/* Output Box */}
                    <div className="outputBox">
                        <textarea
                            className="outputDisplay"
                            value={output}
                            readOnly
                            placeholder="Output will be displayed here..."
                        />
                    </div>
                </div>
            </div>
            <ToastContainer />
        </div>
    );
}

export default CompilerPage;
