// ProfilePage.js
import React, { useState, useEffect } from 'react';
import { Link, useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './ProfilePage.css';

const ProfilePage = () => {
  const { userId } = useParams();
  const [profileInfo, setProfileInfo] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [editData, setEditData] = useState({ fullName: '', email: '', newPassword: '' });
 const navigate = useNavigate();
  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/profile/${userId}`);
        setProfileInfo(response.data.user); // Adjust according to your response structure
      } catch (error) {
        console.error('Error fetching user profile:', error);
      }
    };

    fetchUser();
  }, [userId]);

  if (!profileInfo) {
    return <p>Loading profile...</p>;
  }

  const handleInputChange = (e) => {
    // console.log("handleInput Doing");
    const { name, value } = e.target;
    setEditData({ ...editData, [name]: value });
  };

  const handleUpdate = async () => {
    // console.log("handleUpdate Clicked");
    try {
      const response = await axios.put('http://localhost:5000/update', editData, { withCredentials: true });
      setProfileInfo(response.data.user);
      setIsEditing(false);
    } catch (error) {
      console.error('Error updating user data:', error);
    }
  };

  const handleDelete = async () => {
    // console.log("handleDelete Clicked");
    try {
      const confirmDelete = window.confirm('Are you sure you want to delete your account? This action cannot be undone.');
      if (confirmDelete) {
        const password = prompt('Please enter your password to confirm deletion:');
        if (password) {
          await axios.delete('http://localhost:5000/delete', { withCredentials: true, data: { password } });
          navigate('/'); // Redirect to home page after deletion
        }
      }
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  const handleLogout = async () => {
    try {
      await axios.post('http://localhost:5000/logout', {}, { withCredentials: true });
      navigate('/login'); // Redirect to login page after logout
    } catch (error) {
      console.error('Error logging out:', error);
    }
  }

  return (
    <div>
      {/* HEADER */}
      <div className="headerProfile">
        <div className="left">
          <img src="/Assets/logo.png" alt="Logo" />
        </div>
        <div className="right">
          <Link to="/problemslist">Problems</Link>
          <Link to="/"><img src="/Assets/ProfileLogo.png" className="navProfile" alt="Profile" /></Link>
        </div>
      </div>

      <div className="details">
        .leftSideDetails
        {/* PROFILE DETAILS */}
        

        {/* SOLVED PROBLEMS */}
        <div className="solvedProblems">
          <h2>Solved Problems</h2>
          {profileInfo.solvedProblems && profileInfo.solvedProblems.length > 0 ? (
            <ul>
              {profileInfo.solvedProblems.map((problem, index) => (
                <li key={index}>
                  <Link to={`/problems/${problem._id}`}>{problem.title}</Link> - {problem.difficulty}
                </li>
              ))}
            </ul>
          ) : (
            <p>No problems solved yet.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
