import React, { useState } from "react"; 
import { useNavigate, Link } from "react-router-dom"; 
import axios from "axios"; 
import { ToastContainer, toast } from "react-toastify"; // IMPORT TOASTIFY FOR NOTIFICATIONS
import 'react-toastify/dist/ReactToastify.css'; 
import './LoginPage.css';
import googleLogo from "./GoogleLogo.png";

function LoginPage() {
    const navigate = useNavigate(); 
    const [inputValue, setInputValue] = useState({ // STATE TO STORE FORM INPUTS
        email: "",
        password: "",
    });

    const { email, password } = inputValue; // DESTRUCTURE STATE VARIABLES

    // HANDLE INPUT CHANGE
    const handleOnChange = (e) => {
        const { name, value } = e.target;
        setInputValue({
            ...inputValue,
            [name]: value,
        });
    };

    // DISPLAY ERROR TOAST
    const handleError = (err) =>
        toast.error(err, {
            position: "bottom-left",
        });

    // DISPLAY SUCCESS TOAST
    const handleSuccess = (msg) =>
        toast.success(msg, {
            position: "bottom-left",
        });

    // HANDLE FORM SUBMISSION
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const { data } = await axios.post(
                "http://localhost:5000/login", // BACKEND LOGIN ROUTE
                { ...inputValue },
                { withCredentials: true }
            );
            const { success, message } = data; // DESTRUCTURE RESPONSE
            if (success) { // HANDLE SUCCESS
                handleSuccess(message);
                setTimeout(() => {
                    navigate("/"); // REDIRECT TO HOME PAGE ON SUCCESS
                }, 1000);
            } 
            else { // HANDLE ERROR
                handleError(message);
            }
        } 
        catch (error) {
            console.log(error);
        }
        setInputValue({
            ...inputValue,
            email: "",
            password: "",
        });
    };

    return (
        <div className="LoginPage">
            <div className="header">
                <img src="/Assets/logo.png" alt="Logo" />
            </div>
            <div className="content">
                <h2>Log In</h2>
                <form onSubmit={handleSubmit}> {/* ADDED FORM SUBMISSION */}
                    <div className="boxes">
                        <h5 clssName="top">Email</h5> {/* UPDATED TEXT */}
                        <input
                            type="email" // CHANGED TYPE TO EMAIL
                            name="email"
                            value={email}
                            placeholder="Enter your email"
                            onChange={handleOnChange} // HANDLE INPUT CHANGE
                        />
                        <h5>Password</h5>
                        <input
                            type="password" // CHANGED TYPE TO PASSWORD
                            name="password"
                            value={password}
                            placeholder="Enter your password"
                            onChange={handleOnChange} // HANDLE INPUT CHANGE
                        />
                        <button className="loginPg" type="submit">Log In</button> {/* ADDED TYPE SUBMIT */}
                        <p className="checking">or</p>
                        <button className="google">
                            <img src={googleLogo} alt="Google Logo" className="google-logo" />Continue with Google
                        </button>
                    </div>
                </form>
                <p className="last">Don't have an account?</p>
                <Link to="/signup" className="last">Sign up</Link> {/* UPDATED LINK */}
            </div>
            <ToastContainer /> {/* ADDED TOAST CONTAINER */}
        </div>
    );
}

export default LoginPage;
