import React, { useState } from 'react';
import axios from 'axios';
import './AddProblemCSS.css';

function AddProblemPage() {
  const [problemData, setProblemData] = useState({
    title: '',
    difficulty: '',
    description: '',
    testCases: [{ input: '', output: '' }],
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProblemData({
      ...problemData,
      [name]: value,
    });
  };

  const handleTestCaseChange = (index, e) => {
    const { name, value } = e.target;
    const newTestCases = [...problemData.testCases];
    newTestCases[index][name] = value;
    setProblemData({ ...problemData, testCases: newTestCases });
  };

  const addTestCase = () => {
    setProblemData({
      ...problemData,
      testCases: [...problemData.testCases, { input: '', output: '' }],
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/problems', problemData);
      console.log('Problem added:', response.data);
      // Reset form
      setProblemData({
        title: '',
        difficulty: '',
        description: '',
        testCases: [{ input: '', output: '' }],
      });
    } catch (error) {
      console.error('Error adding problem:', error);
    }
  };

  return (
    <div className="add-problem-page">
      <h1>Add New Problem</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Title:
          <input type="text" name="title" value={problemData.title} onChange={handleChange} required />
        </label>
        <label>
          Difficulty:
          <input type="text" name="difficulty" value={problemData.difficulty} onChange={handleChange} required />
        </label>
        <label>
          Description:
          <textarea name="description" value={problemData.description} onChange={handleChange} required />
        </label>
        <div className="test-cases">
          <h3>Test Cases</h3>
          {problemData.testCases.map((testCase, index) => (
            <div key={index}>
              <label>
                Input:
                <input type="text" name="input" value={testCase.input} onChange={(e) => handleTestCaseChange(index, e)} required />
              </label>
              <label>
                Output:
                <input type="text" name="output" value={testCase.output} onChange={(e) => handleTestCaseChange(index, e)} required />
              </label>
            </div>
          ))}
          <button type="button" onClick={addTestCase}>Add Test Case</button>
        </div>
        <button type="submit">Add Problem</button>
      </form>
    </div>
  );
}

export default AddProblemPage;
