import React, { useState } from 'react';
import { useParams } from 'react-router-dom';

function ProblemDetail({ problems }) {
  const { id } = useParams();
  const problem = problems.find((problem) => problem.id === parseInt(id));

  const [language, setLanguage] = useState('JavaScript');
  const [code, setCode] = useState('');
  const [output, setOutput] = useState('');

  if (!problem) {
    return <div>Problem not found</div>;
  }

  const handleRun = () => {
    // Simulate running code and checking against test cases
    const passed = problem.testCases.every(testCase => {
      // For simplicity, we're just checking if the code matches the expected output
      return code === testCase.output;
    });
    setOutput(passed ? 'Accepted' : 'Wrong Answer');
  };

  const handleSubmit = () => {
    // Simulate submitting code
    handleRun();
  };

  return (
    <div style={{ display: 'flex' }}>
      <div style={{ flex: 3, padding: '20px' }}>
        <h2>{problem.id}. {problem.name}</h2>
        <p><strong>Difficulty:</strong> {problem.difficulty}</p>
        <p>{problem.description}</p>
        <h3>Test Cases:</h3>
        {problem.testCases.map((testCase, index) => (
          <div key={index}>
            <p><strong>Input:</strong> {testCase.input}</p>
            <p><strong>Output:</strong> {testCase.output}</p>
          </div>
        ))}
      </div>
      <div style={{ flex: 2, padding: '20px', borderLeft: '1px solid #ddd' }}>
        <div>
          <label>Language: </label>
          <select value={language} onChange={(e) => setLanguage(e.target.value)}>
            <option value="JavaScript">JavaScript</option>
            <option value="Python">Python</option>
            <option value="Java">Java</option>
          </select>
        </div>
        <div>
          <textarea
            value={code}
            onChange={(e) => setCode(e.target.value)}
            placeholder="Write your code here..."
            rows="10"
            style={{ width: '100%', marginTop: '10px' }}
          />
        </div>
        <div>
          <button onClick={handleRun}>Run</button>
          <button onClick={handleSubmit}>Submit</button>
        </div>
        <div>
          <h3>Output:</h3>
          <pre>{output}</pre>
        </div>
      </div>
    </div>
  );
}

export default ProblemDetail;
