import React, { useState, useEffect } from 'react';
import { Link, UNSAFE_DataRouterContext, useNavigate, useParams} from 'react-router-dom';
import axios from 'axios';
import './ProfilePage.css';

const ProfilePage = () => {
    const navigate = useNavigate()
    const {userId} = useParams();
    const [profileInfo, setProfileInfo] = useState(null);
    const [solvedProblems, setSolvedProblems] = useState([]);

    useEffect(() => {
      const fetchUser = async () => {
          try {
              const response = await axios.get(`http://localhost:5000/profile/${userId}`);
              setProfileInfo(response.data); 
              if(response.data.solvedProblems) {
                  setSolvedProblems(response.data.solvedProblems);
              }
          } catch (error) {
              console.error('Error fetching user data:', error);
              navigate('/login'); // Redirect to login if error occurs
          }
      };

      if(userId) fetchUser();
    }, [userId, navigate]);

  return(
    <div>
        {/* HEADER */}
        <div className="headerComp">
                <div className="left">
                    <img src="/Assets/logo.png" alt="Logo" />
                </div>
                <div className="right">
                    <Link to="/problems">Problems</Link>
                    <Link to="/"><img src="/Assets/ProfileLogo.png" className="profileLogo" alt="Logo" /></Link>
                </div>
            </div>
        {/* DETAILS  */}
        <div className="details">
                {profileInfo ? (
                    <>
                        <h1>{profileInfo.fullName}</h1>
                        <p>Email: {profileInfo.email}</p>

                        {/* Display Solved Problems */}
                        {solvedProblems.length > 0 ? (
                            <div>
                                <h2>Solved Problems:</h2>
                                <ul>
                                    {solvedProblems.map((problem, index) => (
                                        <li key={index}>
                                            <Link to={`/problems/${problem.id}`}>
                                                {problem.title}
                                            </Link>
                                        </li>
                                    ))}
                                </ul>
                            </div>
                        ) : (
                            <p>No solved problems yet.</p>
                        )}
                    </>
                ) : (
                    <p>Loading profile...</p>
                )}
        </div>
    </div>
  );
};

export default ProfilePage;
