import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import './ProfilePage.css';

const ProfilePage = () => {
    const { userId } = useParams();
    const [profileInfo, setProfileInfo] = useState(null); // Initial state set to null
    const [solvedProblems, setSolvedProblems] = useState([]); // State to store solved problems
    const navigate = useNavigate();

    useEffect(() => {
        const fetchUser = async () => {
            try {
                // Log to check if userId is correct
                console.log('Fetching data for userId:', userId);

                const response = await axios.get(`http://localhost:5000/profile/${userId}`);

                // Log the full response to see what the API is returning
                console.log('API Response:', response);

                // Ensure the data exists
                if (response.data) {
                    const user = response.data;

                    // Log user data to verify structure
                    console.log('User data:', user);

                    setProfileInfo(user); // Properly set the user profile info
                    if (user.solvedProblems) {
                        setSolvedProblems(user.solvedProblems); // Set solved problems if they exist
                    }
                } else {
                    console.error('No data found in response');
                }
            } catch (error) {
                console.error('Error fetching user data:', error);
                navigate('/login'); // Redirect to login if error occurs
            }
        };

        // Only fetch data if userId is defined
        if (userId) {
            fetchUser();
        }
    }, [userId, navigate]); // Added dependencies to useEffect

    return (
        <div>
            {/* HEADER */}
            <div className="headerComp">
                <div className="left">
                    <img src="/Assets/logo.png" alt="Logo" />
                </div>
                <div className="right">
                    <Link to="/problems">Problems</Link>
                    <Link to={`/profile/${userId}`}><img src="/Assets/ProfileLogo.png" className="profileLogo" alt="Profile" /></Link>
                </div>
            </div>

            {/* DETAILS */}
            <div className="details">
                {profileInfo ? (
                    <>
                        <h1>{profileInfo.fullName}</h1>
                        <p>Email: {profileInfo.email}</p>

                        {/* Display Solved Problems */}
                        {solvedProblems.length > 0 ? (
                            <div>
                                <h2>Solved Problems:</h2>
                                <ul>
                                    {solvedProblems.map((problem, index) => (
                                        <li key={index}>
                                            <Link to={`/problems/${problem.id}`}>
                                                {problem.title}
                                            </Link>
                                        </li>
                                    ))}
                                </ul>
                            </div>
                        ) : (
                            <p>No solved problems yet.</p>
                        )}
                    </>
                ) : (
                    <p>Loading profile...</p>
                )}
            </div>
        </div>
    );
};

export default ProfilePage;
