import React, { useState, useEffect } from 'react';
import { Link, UNSAFE_DataRouterContext, useNavigate, useParams} from 'react-router-dom';
import axios from 'axios';
import './ProfilePage.css';

const ProfilePage = () => {
    const {userId} = useParams();
    const [profileInfo, setProfileInfo] = useState(null);
    const [solvedProblems, setSolvedProblems] = useState([]);

    useEffect(() => {
      const fetchUser = async () => {
          try {
              const response = await axios.get(`http://localhost:5000/profile/${userId}`);
              setProfileInfo(response.data); // Properly access response data
              if (response.data.solvedProblems) {
                  setSolvedProblems(response.data.solvedProblems); // Set solved problems if available
              }
          } catch (error) {
              console.error('Error fetching user data:', error);
              navigate('/login'); // Redirect to login if error occurs
          }
      };

      if (userId) {
          fetchUser();
      }
    }, [userId, navigate]);

  return(
    <div>
        {/* HEADER */}
        <div className="headerComp">
                <div className="left">
                    <img src="/Assets/logo.png" alt="Logo" />
                </div>
                <div className="right">
                    <Link to="/problems">Problems</Link>
                    <Link to="/"><img src="/Assets/ProfileLogo.png" className="profileLogo" alt="Logo" /></Link>
                </div>
            </div>
        {/* DETAILS  */}
        <div className="details">
            <h1>{profileInfo.data.fullName}</h1>
            {/* <p>Username: {profileInfo.data.username}</p> */}
            <p>Email: {profileInfo.data.email}</p>
        </div>
    </div>
  );
};

export default ProfilePage;
