import React, { useState, useEffect } from 'react';
import { Link, useParams } from "react-router-dom";
import axios from 'axios';
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';
import './CompilerPageCSS.css';

function CompilerPage() {
    const { problemId } = useParams();
    const [problem, setProblem] = useState(null);
    const [selectedLanguage, setSelectedLanguage] = useState("cpp");
    const [code, setCode] = useState({
        lang: "cpp",
        code: "",
    });
    const [output, setOutput] = useState("");
  
    useEffect(() => {
        const fetchProblem = async () => {
            try {
                const response = await axios.get(`http://localhost:8000/compiler/${problemId}`);
                setProblem(response.data.problem);
            } catch (error) {
                console.error('Error fetching problem data:', error);
                toast.error('Error fetching problem data', {
                    position: "top-center",
                });
            }
        };
        fetchProblem();
    }, [problemId]);
  
    const handleCodeChange = (event) => {
        setCode({
            ...code,
            code: event.target.value,
        });
    };
  
    const handleRunCode = async () => {
        try {
            const response = await axios.post('http://localhost:8000/compiler/run', {
                lang: selectedLanguage,
                code: code.code,
            });
            setOutput(response.data.output);
        } catch (error) {
            console.error('Error running code:', error);
            toast.error('Error running code', {
                position: "top-center",
            });
        }
    };
  
    if (!problem) {
        return <div>Loading...</div>;
    }
  
    return (
        <div className="compiler-page">
            <ToastContainer />
            <div className="header">
                <Link to="/problems">Back to Problems</Link>
                <h1>{problem.title}</h1>
            </div>
            <div className="problem-details">
                <h2>Description</h2>
                <p>{problem.description}</p>
                {/* Render other problem details here */}
            </div>
            <div className="code-editor">
                <h2>Code Editor</h2>
                <textarea value={code.code} onChange={handleCodeChange} />
                <div className="controls">
                    <select value={selectedLanguage} onChange={(e) => setSelectedLanguage(e.target.value)}>
                        <option value="cpp">C++</option>
                        <option value="python">Python</option>
                        <option value="javascript">JavaScript</option>
                        <option value="java">Java</option>
                    </select>
                    <button onClick={handleRunCode}>Run Code</button>
                </div>
                <div className="output">
                    <h2>Output</h2>
                    <pre>{output}</pre>
                </div>
            </div>
        </div>
    );
}

export default CompilerPage;
