// AddProblemPage.js
import React, { useState } from 'react';
import axios from 'axios';
import './AddProblemCSS.css';

const AddProblemPage = () => {
  const [problem, setProblem] = useState({
    title: '',
    difficulty: '',
    acceptance_rate: '',
    description: '',
    testCases: [{ input: '', output: '' }]
  });

  const handleChange = (e) => {
    setProblem({
      ...problem,
      [e.target.name]: e.target.value
    });
  };

  const handleTestCaseChange = (e, index) => {
    const newTestCases = problem.testCases.map((testCase, i) => (
      i === index ? { ...testCase, [e.target.name]: e.target.value } : testCase
    ));
    setProblem({
      ...problem,
      testCases: newTestCases
    });
  };

  const addTestCase = () => {
    setProblem({
      ...problem,
      testCases: [...problem.testCases, { input: '', output: '' }]
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/problems', problem);
      console.log('Problem added:', response.data);
      setProblem({
        title: '',
        difficulty: '',
        acceptance_rate: '',
        description: '',
        testCases: [{ input: '', output: '' }]
      });
    } catch (error) {
      console.error('Error adding problem:', error);
    }
  };

  return (
    <div className="add-problem">
      <h1>Add New Problem</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="title"
          value={problem.title}
          onChange={handleChange}
          placeholder="Title"
          required
        />
        <select
          name="difficulty"
          value={problem.difficulty}
          onChange={handleChange}
          required
        >
          <option value="">Select Difficulty</option>
          <option value="Easy">Easy</option>
          <option value="Medium">Medium</option>
          <option value="Hard">Hard</option>
        </select>
        <input
          type="number"
          name="acceptance_rate"
          value={problem.acceptance_rate}
          onChange={handleChange}
          placeholder="Acceptance Rate"
          required
        />
        <textarea
          name="description"
          value={problem.description}
          onChange={handleChange}
          placeholder="Description"
          required
        />
        <h2>Test Cases</h2>
        {problem.testCases.map((testCase, index) => (
          <div key={index}>
            <input
              type="text"
              name="input"
              value={testCase.input}
              onChange={(e) => handleTestCaseChange(e, index)}
              placeholder="Input"
              required
            />
            <input
              type="text"
              name="output"
              value={testCase.output}
              onChange={(e) => handleTestCaseChange(e, index)}
              placeholder="Output"
              required
            />
          </div>
        ))}
        <button type="button" onClick={addTestCase}>Add Test Case</button>
        <button type="submit">Add Problem</button>
      </form>
    </div>
  );
};

export default AddProblemPage;
